import{s as _,e as d,i as l,K as c,d as r,x as w,f as v,g as p,U as m}from"../chunks/scheduler.074f0cd2.js";import{S as y,i as S}from"../chunks/index.2439908b.js";import{l as P}from"../chunks/translations.52632037.js";import{a as h}from"../chunks/about-intro-si.c378e3eb.js";const D=!0;async function O(){return{title_si:"O projektu",title_en:"About the project"}}const V=Object.freeze(Object.defineProperty({__proto__:null,load:O,prerender:D},Symbol.toStringTag,{value:"Module"})),k=""+new URL("../assets/1.de59de54.svg",import.meta.url).href,u=""+new URL("../assets/2.810ae1c6.svg",import.meta.url).href,j=""+new URL("../assets/3.3196ec1f.svg",import.meta.url).href,f=""+new URL("../assets/4.4b4bcd5f.svg",import.meta.url).href,g=""+new URL("../assets/5.b391f212.svg",import.meta.url).href,b=""+new URL("../assets/6.e3ea25a1.svg",import.meta.url).href,z=""+new URL("../assets/7.cb2c79d8.svg",import.meta.url).href;function M(o){let e,t=`<div class="section"><div class="section__inner svelte-19505ds"><img class="intro-icon svelte-19505ds" src="${h}"/> <h1 class="svelte-19505ds">Exploring Careers of Political Officials</h1> <p class="svelte-19505ds">Zvezoskop (roughly translated as Relationscope) is an interactive visualization of careers of government members, ministerial secretaries and Members of Parliament.</p></div></div> <div class="section light-grey svelte-19505ds"><div class="section__inner svelte-19505ds"><h1 class="svelte-19505ds">Why Zvezoskop?</h1> <p class="svelte-19505ds">Slovenia is a small country with a population of 2 million and a widespread belief that personal relationships and loyalty outweigh competence in staffing choices. This perception undermines public trust in government and the Parliament and perpetuates perceived or actual conflicts of interest in the legislative and executive branches.</p> <p class="svelte-19505ds">Zvezoskop provides the public with information about career trajectories of political officials while enabling it to conduct independent research. A decade ago, the German newspaper Die Zeit introduced a similar, albeit simpler, visual representation of government ministers&#39; careers. In Slovenia, this approach is entirely novel.</p> <p class="svelte-19505ds">Following the last four national elections in Slovenia, political parties that were formed (roughly) prior to the elections came to power.</p> <p class="svelte-19505ds">Among the officials featured in Zvezoskop, 40% had never been elected in a general election or appointed as ministers or ministerial secretaries before the 2022 parliamentary elections.</p> <h2 class="svelte-19505ds">The interactive shows:</h2> <ol class="svelte-19505ds"><li class="svelte-19505ds">Links between political officials, allowing the public to discover their career intersections and gain insights.</li> <li class="svelte-19505ds">Verified information on career paths, spanning from educational years to current political roles.</li> <li class="svelte-19505ds">Current and past positions or roles of political officials in other organizations, institutions, or companies, providing an overview of all the politicians associated with an entity.</li> <li class="svelte-19505ds">Insight into the educational, professional, and interest profiles of political parties represented in Government and the Parliament.</li></ol> <h2 class="svelte-19505ds">How can Zvezoskop benefit its users?</h2> <ol class="svelte-19505ds"><li class="svelte-19505ds">Independent exploration of information about education, past employment, and political affiliations of officials.</li> <li class="svelte-19505ds">Discovery of career intersections of current political officials.</li> <li class="svelte-19505ds">Independent research of officials’ career paths while tracking revolving door transitions between the public and private sectors.</li> <li class="svelte-19505ds">Providing voters with access to credible and verified information on the careers of ministers, ministerial secretaries and parliamentarians to assist them in making informed decisions.</li> <li class="svelte-19505ds">A tool for journalists, NGOs, academics, and other researchers conducting independent research into political careers and networks.</li></ol></div></div> <div class="section medium-grey svelte-19505ds"><div class="section__inner svelte-19505ds"><h1 class="svelte-19505ds">Project methodology</h1> <p class="svelte-19505ds">In a small country like Slovenia, career and life paths of various political officials are intricately connected, spanning their educational years, previous employers, political affiliations, and leisure activities.</p> <p class="svelte-19505ds">The team behind Zvezoskop began research by utilizing publicly available CVs of active political officials. Then they employed investigative and data journalism techniques to supplement and upgrade this information.</p> <h5 class="svelte-19505ds"><img class="icon" src="${k}"/> <span>Research</span></h5> <p class="svelte-19505ds">The team meticulously collected, verified, and analyzed data pertaining to career paths of 160 political officials.</p> <h5 class="svelte-19505ds"><img class="icon" src="${u}"/> <span>Data sources</span></h5> <p class="svelte-19505ds">Data was verified and supplemented against official press releases, public and specialized databases, social networks and other investigative work.</p> <h5 class="svelte-19505ds"><img class="icon" src="${j}"/> <span>Evidence</span></h5> <p class="svelte-19505ds">Data available in the interactive is sourced from official documents, data and information, and corroboration by individual officials.</p> <h5 class="svelte-19505ds"><span>Start and End Date</span></h5> <p class="svelte-19505ds">Only data for which we could verify the start and end dates (at least the year) is included in the analysis. These parameters are crucial for predicting intersections between individuals.</p> <h5 class="svelte-19505ds"><span>Institution</span></h5> <p class="svelte-19505ds">The term &quot;institution&quot; refers to locations where individuals intermingle and does not necessarily denote actual legal entities.</p> <h5 class="svelte-19505ds"><img class="icon" src="${g}"/> <span>Right of Reply</span></h5> <p class="svelte-19505ds">All political officials were presented with a timely request to reply to the collected data, and invited to provide corrections and additions, if necessary.</p> <h5 class="svelte-19505ds"><img class="icon" src="${f}"/> <span>Double-checking</span></h5> <p class="svelte-19505ds">To ensure accuracy, all data underwent double-checking by journalists. Two team members independently collected information on career trajectories of each official by utilizing investigative and data journalism techniques.</p> <h5 class="svelte-19505ds"><img class="icon" src="${b}"/> <span>Fact-checking</span></h5> <p class="svelte-19505ds">In compliance with Oštro’s <a target="_blank" href="//ostro.si/en/code-of-conduct" class="svelte-19505ds">Code of Conduct</a>, the data underwent internal verification before publication by team members who were not involved in its collection. All evidence was archived and is securely stored by the newsroom.</p> <h5 class="svelte-19505ds"><img class="icon" src="${z}"/> <span>Open data</span></h5> <p class="svelte-19505ds">After publication, we will upload the verified data to the free and openly accessible Wikidata and OpenSanctions data repositories.</p> <p class="svelte-19505ds">Wikidata is governed by a community of users and managed by the Wikipedia Foundation, and serves as a central repository for structured data from various sister projects.</p> <p class="svelte-19505ds">OpenSanctions is an international repository of information on individuals and entities linked by politics, crime, or economic interests.</p> <p class="svelte-19505ds">By contributing to these repositories, we aim to promote open access to information for the broader international community.</p> <h5 class="svelte-19505ds"><span>Other</span></h5> <p class="svelte-19505ds">The data is current as of 1 February 2024. Due to limited staff, Oštro cannot guarantee updates of the data at all times.</p> <p class="svelte-19505ds">For additional information about career paths of officials, please contact us at contact@ostro.si or by post at Oštro, Kotnikova ulica 28, 1000 Ljubljana, Slovenia.</p></div></div> <div class="section dark-grey svelte-19505ds"><div class="section__inner svelte-19505ds"><h1 class="svelte-19505ds">Impressum</h1> <div class="two-columns svelte-19505ds"><div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Editor in chief</p> <p class="svelte-19505ds">Anuška Delić</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Project leader</p> <p class="svelte-19505ds">Klara Škrinjar</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Authors</p> <p class="svelte-19505ds">Maja Čakarić, Samo Demšar, Žana Erznožnik, Martin Justin, Nina Rozman, Klara Škrinjar, Matej Zwitter</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Fact-checking</p> <p class="svelte-19505ds">Maja Čakarić, Samo Demšar, Žana Erznožnik, Nina Rozman, Klara Škrinjar, Matej Zwitter, Katarina Bulatović</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Expert consultants</p> <p class="svelte-19505ds">dr. Anuška Ferligoj, dr. Vladimir Batagelj</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Front-end development</p> <p class="svelte-19505ds">Kirk G. Jackson</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Data wrangling</p> <p class="svelte-19505ds">Jan Daniel Bothma, Friedrich Lindenberg</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Translation</p> <p class="svelte-19505ds">Sebastijan Maček</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Design</p> <p class="svelte-19505ds"><a target="_blank" href="//mashoni.com/" class="svelte-19505ds">Studio Mashoni</a></p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">The project was co-financed by</p> <p class="svelte-19505ds">Google News Initiative Innovation Challenge</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Contact</p> <p class="svelte-19505ds">contact@ostro.si</p></div></div> <p class="svelte-19505ds"><a target="_blank" href="https://www.ostro.si/" class="svelte-19505ds">Oštro © 2024</a></p></div></div>`;return{c(){e=v("div"),e.innerHTML=t},l(s){e=p(s,"DIV",{"data-svelte-h":!0}),m(e)!=="svelte-1sjk2z2"&&(e.innerHTML=t)},m(s,i){l(s,e,i)},d(s){s&&r(e)}}}function Z(o){let e,t=`<div class="section"><div class="section__inner svelte-19505ds"><img class="intro-icon svelte-19505ds" src="${h}"/> <h1 class="svelte-19505ds">Po poklicnih poteh političnih funkcionarjev</h1> <p class="svelte-19505ds">Zvezoskop je interaktivna vizualizacija kariernih presečišča članov vlade, državnih sekretarjev in poslancev državnega zbora.</p></div></div> <div class="section light-grey svelte-19505ds"><div class="section__inner svelte-19505ds"><h1 class="svelte-19505ds">Zakaj Zvezoskop?</h1> <p class="svelte-19505ds">Slovenija je majhna država z dvema milijonoma prebivalcev, v slovenski javnosti pa je razširjeno mnenje, da so pri kadrovskih odločitvah pomembnejši osebni odnosi in lojalnost kot kompetence. To spodkopava zaupanje ljudi v vlado in državni zbor ter ustvarja tveganja za navidezna ali dejanska navzkrižja interesov med predstavniki zakonodajne in izvršilne oblasti.</p> <p class="svelte-19505ds">Projekt Zvezoskop smo zasnovali, da bi javnosti omogočili vpogled v podatke o karierah političnih funkcionarjev in samostojno raziskovanje njihovih dosedanjih poklicnih poti. Pred desetletjem je nemški časopis Die Zeit ustvaril podoben, vendar bolj preprost <a target="_blank" href="//driven-by-data.net/2013/09/19/bundeskabinett.html" class="svelte-19505ds">vizualni prikaz</a> karier ministrov tedanje vlade. V primeru Zvezoskopa gre za povsem svež pristop k obravnavi te teme v Sloveniji.</p> <p class="svelte-19505ds">Po zadnjih štirih državnozborskih volitvah v Sloveniji so na oblast prišle politične stranke, ki so nastale bolj ali manj pred volitvami.</p> <p class="svelte-19505ds">Med funkcionarji, katerih kariere predstavljamo v interaktivni vizualizaciji, je 40 odstotkov takih, ki do državnozborskih volitev 2022 niso bili izvoljeni na splošnih volitvah ali imenovani za ministre oziroma državne sekretarje.</p> <h2 class="svelte-19505ds">Kaj prikazuje interaktivna vizualizacija?</h2> <ol class="svelte-19505ds"><li class="svelte-19505ds">Povezave med političnimi funkcionarji, da bi javnosti omogočili samostojno odkrivanje presečišč njihovih dosedanjih poklicnih poti.</li> <li class="svelte-19505ds">Preverjene podatke o poklicnih poteh, od izobraževanja do današnje politične funkcije.</li> <li class="svelte-19505ds">Aktualne in pretekle položaje oziroma vloge funkcionarjev v drugih organizacijah, institucijah ali podjetjih, kar omogoča pregled nad vsemi politiki, ki jih povezuje posamezna organizacija.</li> <li class="svelte-19505ds">Vpogled v izobrazbene, poklicne in interesne profile političnih strank, ki so zastopane v vladi in državnem zboru.</li></ol> <h2 class="svelte-19505ds">Kako lahko projekt Zvezoskop koristi uporabnikom?</h2> <ol class="svelte-19505ds"><li class="svelte-19505ds">Na preprost način lahko samostojno raziskujejo podatke o izobrazbi političnih funkcionarjev, preteklih zaposlitvah in članstvu v političnih strankah in drugih interesnih skupinah.</li> <li class="svelte-19505ds">Pri tem lahko spremljajo, kako se je dosedanja poklicna pot aktualnega funkcionarja prepletala z drugimi funkcionarji.</li> <li class="svelte-19505ds">Samostojno lahko raziskujejo karierne poti funkcionarjev in spremljajo prehajanje kadrov iz javnega v zasebni sektor in nasprotno.</li> <li class="svelte-19505ds">Volivcem omogoča dostop do verodostojnih in preverjenih podatkov o karieri poslancev, ministrov in državnih sekretarjev, ki jim bodo v pomoč pri sprejemanju informiranih odločitev.</li> <li class="svelte-19505ds">Za novinarje, nevladne organizacije, akademike in druge raziskovalce je delovno orodje pri morebitnem samostojnem raziskovanju političnih poti in omrežij.</li></ol></div></div> <div class="section medium-grey svelte-19505ds"><div class="section__inner svelte-19505ds"><h1 class="svelte-19505ds">Metodologija projekta</h1> <p class="svelte-19505ds">Karierne in življenjske poti različnih političnih funkcionarjev so v mali državi, kot je Slovenija, močno prepletene, naj gre za institucije, kjer so se izobraževali, prejšnje delodajalce, politične stranke ali prosti čas. Ekipa projekta Zvezoskop je za delovno izhodišče vzela javno objavljene življenjepise političnih funkcionarjev, nato pa jih je z uporabo preiskovalnih in podatkovnih novinarskih tehnik dopolnila in nadgradila.</p> <h5 class="svelte-19505ds"><img class="icon" src="${k}"/> <span>Preiskava</span></h5> <p class="svelte-19505ds">Ekipa je zbirala, preverila in analizirala podatke o kariernih poteh 160 političnih funkcionarjev.</p> <h5 class="svelte-19505ds"><img class="icon" src="${u}"/> <span>Viri podatkov</span></h5> <p class="svelte-19505ds">Podatke smo preverjali in dopolnjevali na podlagi javnih objav, jih iskali v registrih in specializiranih podatkovnih bazah, medijskih arhivih, na družbenih omrežjih in s preiskovalnim novinarskim delom.</p> <h5 class="svelte-19505ds"><img class="icon" src="${j}"/> <span>Dokazi</span></h5> <p class="svelte-19505ds">Podatki, vključeni v interaktivno vizualizacijo, temeljijo na uradnih dokumentih in podatkih, pa tudi na potrditvah posameznih funkcionarjev.</p> <h5 class="svelte-19505ds"><span>Začetni in končni datum</span></h5> <p class="svelte-19505ds">V analizo so vključeni le podatki, za katere smo lahko določili datuma vstopa v institucijo in izstopa iz nje (vsaj letnico). Presečišč med posamezniki brez teh spremenljivk ni mogoče predvideti.</p> <h5 class="svelte-19505ds"><span>Institucija</span></h5> <p class="svelte-19505ds">Izraz institucija uporabljamo za kraje, kjer se osebe srečujejo zaradi poklicnih zadev pa tudi hobijev, zato njihova imena niso nujno imena pravnih oseb.</p> <h5 class="svelte-19505ds"><img class="icon" src="${g}"/> <span>Pravica do odgovora</span></h5> <p class="svelte-19505ds">Vse politične funkcionarje smo pravočasno seznanili z zbranimi podatki ter jih prosili za popravke in dopolnitve.</p> <h5 class="svelte-19505ds"><img class="icon" src="${f}"/> <span>Dvojno preverjanje</span></h5> <p class="svelte-19505ds">Novinarji so vse podatke dvojno preverili, kar pomeni, da sta podatke o kariernih poteh posameznega funkcionarja neodvisno zbirala dva člana ekipe, pri čemer sta uporabljala tehnike preiskovalnega in podatkovnega novinarstva.</p> <h5 class="svelte-19505ds"><img class="icon" src="${b}"/> <span>Preverjanje dejstev</span></h5> <p class="svelte-19505ds">Skladno s <a href="//www.ostro.si/si/kodeks" target="_blank" class="svelte-19505ds">kodeksom</a> Oštra so verodostojnost podatkov pred objavo preverili še člani ekipe, ki niso sodelovali pri njihovem pridobivanju. Vse dokaze smo arhivirali in jih hranimo v uredništvu.</p> <h5 class="svelte-19505ds"><img class="icon" src="${z}"/> <span>Odprte zbirke podatkov</span></h5> <p class="svelte-19505ds">Preverjene podatke bomo po objavi projekta naložili tudi v brezplačni in prosto dostopni podatkovni zbirki Wikidata in OpenSanctions.</p> <p class="svelte-19505ds">Wikidata, ki jo ureja skupnost uporabnikov, upravlja pa Wikipedia Foundation, deluje kot osrednje skladišče za strukturirane podatke sestrskih projektov, kot so Wikipedia, Wikivoyage, Wikisource in drugi.</p> <p class="svelte-19505ds">OpenSanctions je prosto dostopna mednarodna zbirka podatkov o ljudeh in podjetjih, ki jih povezujejo politika, kriminal ali ekonomski interes.</p> <p class="svelte-19505ds">Zvezoskop je tako v korist širše mednarodne skupnosti prispeval tudi k odprtemu dostopu do informacij.</p> <h5 class="svelte-19505ds"><span>Drugo</span></h5> <p class="svelte-19505ds">V podatkovni bazi prikazujemo podatke na dan 1. 2. 2024. Oštro zaradi omejenih kadrovskih zmogljivosti ne more v vsakem trenutku zagotavljati aktualnosti podatkov.</p> <p class="svelte-19505ds">Dodatne informacije o poklicnih poteh funkcionarjev nam lahko pošljete na elektronski naslov contact@ostro.si ali po pošti na naslov Oštro, Kotnikova ulica 28, 1000 Ljubljana.</p></div></div> <div class="section dark-grey svelte-19505ds"><div class="section__inner svelte-19505ds"><h1 class="svelte-19505ds">Kolofon</h1> <div class="two-columns svelte-19505ds"><div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Odgovorna urednica</p> <p class="svelte-19505ds">Anuška Delić</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Vodja projekta</p> <p class="svelte-19505ds">Klara Škrinjar</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Avtorji</p> <p class="svelte-19505ds">Maja Čakarić, Samo Demšar, Žana Erznožnik, Martin Justin, Nina Rozman, Klara Škrinjar, Matej Zwitter</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Preverjanje dejstev</p> <p class="svelte-19505ds">Maja Čakarić, Samo Demšar, Žana Erznožnik, Nina Rozman, Klara Škrinjar, Matej Zwitter, Katarina Bulatović</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Strokovna pomoč pri podatkovni analizi</p> <p class="svelte-19505ds">dr. Anuška Ferligoj, dr. Vladimir Batagelj</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Razvoj platforme</p> <p class="svelte-19505ds">Kirk G. Jackson</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Obdelava podatkov</p> <p class="svelte-19505ds">Jan Daniel Bothma, Friedrich Lindenberg</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Prevod</p> <p class="svelte-19505ds">Sebastijan Maček</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Oblikovanje</p> <p class="svelte-19505ds"><a target="_blank" href="//mashoni.com/" class="svelte-19505ds">Studio Mashoni</a></p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Projekt je sofinanciral</p> <p class="svelte-19505ds">Google News Initiative Innovation Challenge</p></div> <div class="two-columns__item svelte-19505ds"><p class="svelte-19505ds">Kontakt</p> <p class="svelte-19505ds">contact@ostro.si</p></div></div> <p class="svelte-19505ds"><a target="_blank" href="https://www.ostro.si/" class="svelte-19505ds">Oštro © 2024</a></p></div></div>`;return{c(){e=v("div"),e.innerHTML=t},l(s){e=p(s,"DIV",{"data-svelte-h":!0}),m(e)!=="svelte-1jt08wv"&&(e.innerHTML=t)},m(s,i){l(s,e,i)},d(s){s&&r(e)}}}function $(o){let e;function t(a,n){return a[0]==="si"?Z:M}let s=t(o),i=s(o);return{c(){i.c(),e=d()},l(a){i.l(a),e=d()},m(a,n){i.m(a,n),l(a,e,n)},p(a,[n]){s!==(s=t(a))&&(i.d(1),i=s(a),i&&(i.c(),i.m(e.parentNode,e)))},i:c,o:c,d(a){a&&r(e),i.d(a)}}}function I(o,e,t){let s;return w(o,P,i=>t(0,s=i)),[s]}class A extends y{constructor(e){super(),S(this,e,I,$,_,{})}}export{A as component,V as universal};
