import{s as z,w as r,e as g}from"../chunks/scheduler.1b4b2730.js";import{S as _,i as w,e as v,a as l,f as c,g as p,h as d,F as u}from"../chunks/index.3afd4222.js";import{l as y}from"../chunks/translations.a43057f9.js";const X="/zvezoskop/_app/immutable/assets/about-intro-en.c2162727.svg",S="/zvezoskop/_app/immutable/assets/about-intro-si.0edde870.svg",P=!0;async function M(){return{title_si:"O projektu",title_en:"About the project"}}const N=Object.freeze(Object.defineProperty({__proto__:null,load:M,prerender:P},Symbol.toStringTag,{value:"Module"})),h="/zvezoskop/_app/immutable/assets/1.de59de54.svg",m="/zvezoskop/_app/immutable/assets/2.810ae1c6.svg",k="/zvezoskop/_app/immutable/assets/3.3196ec1f.svg",j="/zvezoskop/_app/immutable/assets/4.4b4bcd5f.svg",f="/zvezoskop/_app/immutable/assets/5.b391f212.svg",x="/zvezoskop/_app/immutable/assets/6.e3ea25a1.svg",b="/zvezoskop/_app/immutable/assets/7.cb2c79d8.svg";function I(o){let e,s=`<div class="section"><div class="section__inner svelte-uat3cx"><img class="intro-icon svelte-uat3cx" src="${X}"/> <h1 class="svelte-uat3cx">Tracking career paths of politicians</h1> <p class="svelte-uat3cx">Relatoscope project is an interactive visualization of career paths of current Slovenian political office holders – cabinet ministers, state secretaries and National Assembly deputies.</p></div></div> <div class="section light-grey svelte-uat3cx"><div class="section__inner svelte-uat3cx"><h1 class="svelte-uat3cx">About the project</h1> <p class="svelte-uat3cx">In Slovenia, a small country of 2 million people, the public perception is that personal relationships and loyalty are more important than competence in staffing decisions. This undermines public confidence in the government and the National Assembly and creates potential risks of perceived or actual conflicts of interest between representatives of the legislative and executive branches of government.</p> <p class="svelte-uat3cx">The interactive enables users to track the careers of political office holders, and to identify points where they intersect. It is an entirely novel approach to addressing this topic in Slovenia. A decade ago, the German newspaper Die Zeit created a similar, but simpler, visualization of careers of governmental ministers at the time.</p> <h2 class="svelte-uat3cx">Why Relatoscope?</h2> <p class="svelte-uat3cx">The last four general elections in Slovenia have been won by political parties that were formed just before the elections. The existing proportional electoral system prevents voters from actually picking their representatives and they can end up voting complete strangers into political office.</p> <p class="svelte-uat3cx">Among the ministers, state secretaries and MPs included in the interactive visualization, XXXX% have never been elected at a general election or appointed ministers or secretaries of state. We were interested to see how their previous paths intersect, whether and to what extent other spheres of interest permeate their individual political careers, and similar.</p> <p class="svelte-uat3cx">Oštro collected and analyzed data on career paths of three groups of political office holders whose networks can be explored in the interactive.</p> <h2 class="svelte-uat3cx">What does the interactive show?</h2> <ol class="svelte-uat3cx"><li class="svelte-uat3cx">Links between political office holders to facilitate public insight and enable independent discovery of their career points of convergence.</li> <li class="svelte-uat3cx">Verified information on their career paths, from primary school to their current political office.</li> <li class="svelte-uat3cx">Current and past positions in other organizations, institutions or companies that give an overview of all the politicians associated with a particular organization.</li> <li class="svelte-uat3cx">Insight into the educational, professional and interest-based profiles of political parties represented in the Government and the National Assembly.</li></ol> <h2 class="svelte-uat3cx">How can the project benefit its users?</h2> <ol class="svelte-uat3cx"><li class="svelte-uat3cx">They can easily and independently research information on the education of political office holders, their employment history and memberships in political parties and other interest groups.</li> <li class="svelte-uat3cx">They can track how the career of an office holder has intersected with the careers of other politicians.</li> <li class="svelte-uat3cx">They can independently investigate the career paths of officials and analyze the movement of individuals from the public to the private sector and vice versa.</li> <li class="svelte-uat3cx">It gives voters access to credible and verified information on the careers of MPs, ministers and state secretaries to help them make informed decisions.</li> <li class="svelte-uat3cx">For journalists, NGOs, scholars and other researchers, it is a tool for independent exploration of political trajectories and networks.</li></ol></div></div> <div class="section medium-grey svelte-uat3cx"><div class="section__inner svelte-uat3cx"><h1 class="svelte-uat3cx">Project methodology</h1> <p class="svelte-uat3cx">In a country as small as Slovenia, the career paths and lives of political office holders are deeply intertwined, whether through institutions where they were educated, their previous employers, political parties, or their leisure time. The project team took the published CVs of political office holders as a starting point and then built on them using investigative and data journalism techniques. </p> <h5 class="svelte-uat3cx"><img class="icon" src="${h}"/> <span>Investigation</span></h5> <p class="svelte-uat3cx">The team collected, verified and analyzed data on the career paths of XXXX political officers.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${m}"/> <span>Data sources</span></h5> <p class="svelte-uat3cx">The data was verified and supplemented with the help of various publications, searches in registers and specialized databases, media archives, social networks, and independent investigative work.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${k}"/> <span>Evidence</span></h5> <p class="svelte-uat3cx">Vsak podatek, vključen v interaktivno vizualizacijo, temelji na primarnih virih, uradnih dokumentih in informacijah, pa tudi potrditvah posameznih funkcionarjev.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${j}"/> <span>Double verification</span></h5> <p class="svelte-uat3cx">Journalists double-checked all the data, which means that information about the career paths of each official was collected independently by two members of the team, using both investigative and data journalism techniques.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${f}"/> <span>Right to reply</span></h5> <p class="svelte-uat3cx">All political office holders were informed in good time of the data collected, and asked for corrections and additions.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${x}"/> <span>Fact-checking</span></h5> <p class="svelte-uat3cx">In accordance with Oštro’s <a target="_blank" href="//ostro.si/en/code-of-conduct" class="svelte-uat3cx">Code of Conduct</a>, the veracity of the data was fact-checked before publication by team members who were not involved in data collection. All evidence has been archived and is stored by the newsroom.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${b}"/> <span>Open data</span></h5> <p class="svelte-uat3cx">Once the project is published, the verified data will be uploaded to two public databases, Wikidata and OpenSanctions. Wikidata is a freely accessible community database managed by the Wikipedia Foundation. It acts as a central repository for structured data of its sister projects, such as Wikipedia, Wikivoyage, Wiktionary, Wikisource and others. OpenSanctions is an international database of persons and companies of political, criminal, or economic interest. In this way, Oštro contributed to open access to information for the benefit of the broader international community.</p> <p class="svelte-uat3cx">The database shows data as of XXXX 2023. Due to limited staff capacity, it is difficult to ensure that the data is at all times up-to-date. You can send information about career paths of officials XXXX@ostro.si, or by post to Oštro, Kotnikova ulica 28, 1000 Ljubljana, Slovenia.</p></div></div> <div class="section dark-grey svelte-uat3cx"><div class="section__inner svelte-uat3cx"><h1 class="svelte-uat3cx">Impressum</h1> <div class="two-columns svelte-uat3cx"><div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Editor-in-chief</p> <p class="svelte-uat3cx">Anuška Delić</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Project leader</p> <p class="svelte-uat3cx">Klara Škrinjar</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Authors</p> <p class="svelte-uat3cx">Maja Čakarić, Samo Demšar, Žana Erznožnik, Martin Justin, Nina Rozman, Klara Škrinjar, Matej Zwitter</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Fact checking</p> <p class="svelte-uat3cx">Katarina Bulatović, Maja Čakarić, Samo Demšar, Žana Erznožnik, Nina Rozman, Klara Škrinjar, Matej Zwitter</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Data wrangling</p> <p class="svelte-uat3cx">Jan Daniel Bothma, Friedrich Lindenberg</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Interactive platform</p> <p class="svelte-uat3cx">Kirk G. Jackson</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Design</p> <p class="svelte-uat3cx"><a target="_blank" href="//mashoni.com/" class="svelte-uat3cx">Studio Mashoni</a></p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Translation</p> <p class="svelte-uat3cx">Sebastijan Maček</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Project co-financed by</p> <p class="svelte-uat3cx">Google News Initiative Innovation Challenge</p></div></div></div></div>`;return{c(){e=p("div"),e.innerHTML=s},l(a){e=d(a,"DIV",{"data-svelte-h":!0}),u(e)!=="svelte-197k9k6"&&(e.innerHTML=s)},m(a,i){l(a,e,i)},d(a){a&&c(e)}}}function D(o){let e,s=`<div class="section"><div class="section__inner svelte-uat3cx"><img class="intro-icon svelte-uat3cx" src="${S}"/> <h1 class="svelte-uat3cx">Po poklicnih poteh političnih funkcionarjev</h1> <p class="svelte-uat3cx">Projekt Zvezoskop interaktivno prikazuje poklicne odnose med aktualnimi slovenskimi politiki, njihovimi karierami in podjetji. Novinarji Oštra so v vizualizacijo vključili člane vlade, državne sekretarje in poslance državnega zbora.</p></div></div> <div class="section light-grey svelte-uat3cx"><div class="section__inner svelte-uat3cx"><h1 class="svelte-uat3cx">O projektu</h1> <p class="svelte-uat3cx">Slovenija je majhna država z dvema milijonoma prebivalcev, v kateri je v javnosti razširjeno mnenje, da so pri kadrovskih odločitvah pomembnejši osebni odnosi in lojalnost kot kompetence. To spodkopava zaupanje ljudi v vlado in državni zbor ter ustvarja tveganja za navidezna ali dejanska navzkrižja interesov pri predstavnikih zakonodajno-izvršilne oblasti.</p> <p class="svelte-uat3cx">Projekt Zvezoskop je bil zasnovan z namenom prikazati kariere političnih funkcionarjev in poiskati njihove presečne točke oziroma področja. Gre za povsem svež pristop k obravnavi te teme v Sloveniji. Pred desetletjem je nemški časopis Die Zeit ustvaril podoben, vendar bolj preprost vizualni prikaz karier ministrov tedanje vlade.</p> <h2 class="svelte-uat3cx">Zakaj Zvezoskop?</h2> <p class="svelte-uat3cx">Na zadnjih četvero državnozborskih volitvah v Sloveniji so zmagale politične stranke, ki so nastale pred volitvami. Proporcionalni sistem volivcem onemogoča, da bi dejansko izbrali svoje predstavnike, in tako na politične funkcije lahko pripelje popolne neznance.</p> <p class="svelte-uat3cx">Med člani vlade, državnimi sekretarji in poslanci, ki so zajeti v interaktivno vizualizacijo, je XXXX odstotkov takih, ki nikoli prej niso bili izvoljeni na splošnih volitvah ali imenovani za ministre oziroma državne sekretarje. Zanimalo nas je, kako se njihove dosedanje poti križajo, ali in koliko v posamezno politično kariero pronicajo druge interesne sfere in podobno.</p> <p class="svelte-uat3cx">Oštro je zbral in analiziral podatke o kariernih poteh treh skupin političnih funkcionarjev, njihova omrežja so prikazana v interaktivni vizualizaciji teh podatkov.</p> <h2 class="svelte-uat3cx">Kaj prikazuje interaktivna vizualizacija?</h2> <ol class="svelte-uat3cx"><li class="svelte-uat3cx">Povezave med političnimi funkcionarji, da bi javnosti olajšali vpogled vanje in omogočili samostojno odkrivanje presečišč njihovih dosedanjih poklicnih poti.</li> <li class="svelte-uat3cx">Preverjene podatke o poklicnih poteh, od izobraževanja v osnovni šoli do današnje politične funkcije.</li> <li class="svelte-uat3cx">Aktualne in pretekle pozicije oziroma vloge funkcionarjev v drugih organizacijah, institucijah ali podjetjih, kar omogoča pregled nad vsemi politiki, ki jih povezuje posamezna organizacija.</li> <li class="svelte-uat3cx">Vpogled v izobrazbene, poklicne in interesne profile političnih strank, ki so zastopane v vladi in državnem zboru.</li></ol> <h2 class="svelte-uat3cx">Kako lahko projekt koristi uporabnikom?</h2> <ol class="svelte-uat3cx"><li class="svelte-uat3cx">Na preprost način lahko samostojno raziskujejo podatke o izobrazbi političnih funkcionarjev, preteklih zaposlitvah in članstvu v političnih strankah in drugih interesnih skupinah.</li> <li class="svelte-uat3cx">Pri tem lahko spremljajo, kako se je dosedanja poklicna pot aktualnega funkcionarja prepletala z drugimi funkcionarji.</li> <li class="svelte-uat3cx">Samostojno lahko raziskujejo karierne poti funkcionarjev in spremljajo prehajanje kadrov iz javnega v zasebni sektor in nasprotno.</li> <li class="svelte-uat3cx">Volivcem omogoča dostop do verodostojnih in preverjenih podatkov o karieri poslancev, ministrov in državnih sekretarjev, ki jim bodo v pomoč pri sprejemanju informiranih odločitev.</li> <li class="svelte-uat3cx">Za novinarje, nevladne organizacije, akademike in druge raziskovalce je delovno orodje pri morebitnem samostojnem raziskovanju političnih poti in omrežij.</li></ol></div></div> <div class="section medium-grey svelte-uat3cx"><div class="section__inner svelte-uat3cx"><h1 class="svelte-uat3cx">Metodologija projekta</h1> <p class="svelte-uat3cx">Karierne in življenjske poti različnih političnih funkcionarjev so v majhni državi, kot je Slovenija, močno prepletene, naj gre za institucije, kjer so se izobraževali, prejšnje delodajalce, politične stranke ali prosti čas. Ekipa projekta Veze.si je za delovno izhodišče vzela javno objavljene življenjepise političnih funkcionarjev, nato pa jih je z uporabo preiskovalnih in podatkovnih tehnik nadgradila.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${h}"/> <span>Preiskava</span></h5> <p class="svelte-uat3cx">Ekipa je zbirala, preverjala in analizirala podatke o kariernih poteh XXXX političnih funkcionarjev.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${m}"/> <span>Viri podatkov</span></h5> <p class="svelte-uat3cx">Podatke smo preverjali in dopolnjevali na podlagi javnih objav, jih iskali v registrih in specializiranih podatkovnih bazah, medijskih arhivih, na družbenih omrežjih in s samostojnim preiskovalnim delom. </p> <h5 class="svelte-uat3cx"><img class="icon" src="${k}"/> <span>Dokazi</span></h5> <p class="svelte-uat3cx">Vsak podatek, vključen v interaktivno vizualizacijo, temelji na primarnih virih, uradnih dokumentih in informacijah, pa tudi potrditvah posameznih funkcionarjev.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${j}"/> <span>Dvojno preverjanje</span></h5> <p class="svelte-uat3cx">Novinarji so vse podatke dvojno preverili, kar pomeni, da sta podatke o kariernih poteh posameznega funkcionarja neodvisno zbirala dva člana ekipe, pri čemer sta uporabljala tehnike preiskovalnega in podatkovnega novinarstva.</p> <h5 class="svelte-uat3cx"><img class="icon" src="${f}"/> <span>Pravica do odgovora</span></h5> <p class="svelte-uat3cx">Vse politične funkcionarje smo pravočasno seznanili z zbranimi podatki ter jih prosili za popravke in dopolnitve. </p> <h5 class="svelte-uat3cx"><img class="icon" src="${x}"/> <span>Preverjanje dejstev</span></h5> <p class="svelte-uat3cx">Skladno s <a target="_blank" href="//ostro.si/en/code-of-conduct" class="svelte-uat3cx">kodeksom</a> Oštra so verodostojnost podatkov pred objavo notranje preverili še člani ekipe, ki niso sodelovali pri njihovem pridobivanju. Vse dokaze smo arhivirali in jih hranimo v uredništvu. </p> <h5 class="svelte-uat3cx"><img class="icon" src="${b}"/> <span>Wikidata</span></h5> <p class="svelte-uat3cx">Preverjene podatke bomo po objavi projekta naložili tudi v brezplačno in prosto dostopno podatkovno zbirko Wikidata, ki jo ureja skupnost uporabnikov, upravlja pa Wikipedia Foundation. Wikidata deluje kot osrednje skladišče za strukturirane podatke njenih sestrskih projektov, kot so Wikipedija, Wikivoyage, Wiktionary, Wikisource in drugi. Tako bomo k odprtemu dostopu do informacij prispevali tudi v korist širše mednarodne skupnosti.</p> <p class="svelte-uat3cx">V podatkovni bazi prikazujemo podatke na dan XXXX 2023. Oštro zaradi omejenih kadrovskih zmogljivosti ne more zagotavljati aktualnosti podatkov v vsakem trenutku. 
					Dodatne informacije o poklicnih poteh funkcionarjev nam lahko pošljete na elektronski naslov XXXX@ostro.si ali po pošti na naslov Oštro, Kotnikova ulica 28, 1000 Ljubljana.</p></div></div> <div class="section dark-grey svelte-uat3cx"><div class="section__inner svelte-uat3cx"><h1 class="svelte-uat3cx">Kolofon</h1> <div class="two-columns svelte-uat3cx"><div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Odgovorna urednica</p> <p class="svelte-uat3cx">Anuška Delić</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Vodja projekta</p> <p class="svelte-uat3cx">Klara Škrinjar</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Avtorji</p> <p class="svelte-uat3cx">Maja Čakarić, Samo Demšar, Žana Erznožnik, Martin Justin, Nina Rozman, Klara Škrinjar, Matej Zwitter</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Preverjanje dejstev</p> <p class="svelte-uat3cx">Katarina Bulatović, Maja Čakarić, Samo Demšar, Žana Erznožnik, Nina Rozman, Klara Škrinjar, Matej Zwitter</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Prenos v javne podatkovne zbirke</p> <p class="svelte-uat3cx">Jan Daniel Bothma, Friedrich Lindenberg</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Razvoj platforme</p> <p class="svelte-uat3cx">Kirk G. Jackson</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Oblikovanje</p> <p class="svelte-uat3cx"><a target="_blank" href="//mashoni.com/" class="svelte-uat3cx">Studio Mashoni</a></p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Prevod</p> <p class="svelte-uat3cx">Sebastijan Maček</p></div> <div class="two-columns__item svelte-uat3cx"><p class="svelte-uat3cx">Projekt sofinancira</p> <p class="svelte-uat3cx">Google News Initiative Innovation Challenge</p></div></div></div></div>`;return{c(){e=p("div"),e.innerHTML=s},l(a){e=d(a,"DIV",{"data-svelte-h":!0}),u(e)!=="svelte-90rr85"&&(e.innerHTML=s)},m(a,i){l(a,e,i)},d(a){a&&c(e)}}}function O(o){let e;function s(t,n){return t[0]==="si"?D:I}let a=s(o),i=a(o);return{c(){i.c(),e=v()},l(t){i.l(t),e=v()},m(t,n){i.m(t,n),l(t,e,n)},p(t,[n]){a!==(a=s(t))&&(i.d(1),i=a(t),i&&(i.c(),i.m(e.parentNode,e)))},i:r,o:r,d(t){t&&c(e),i.d(t)}}}function T(o,e,s){let a;return g(o,y,i=>s(0,a=i)),[a]}class A extends _{constructor(e){super(),w(this,e,T,O,z,{})}}export{A as component,N as universal};
